import React, { Component } from "react";

import { SearchBar } from "./SearchBar";
import { List } from "./List";

export class InteractiveSection extends Component {
  state = {
    searchTerm: "",
    people: [],
    peopleFilter: []
  };

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then(response => response.json())
      .then(data => {
        this.setState({ people: data });
      });
  }

  handleChange = searchInput => {
    this.setState({ searchTerm: searchInput });
    const result = this.state.people.filter(person => {
      person.name.toLowerCase() === searchInput.toLowerCase();
    });

    console.log(result);
  };

  render() {
    return (
      <div className="Interactive">
        <h3>Star Wars Database</h3>
        <SearchBar handleChange={this.handleChange} />
        <List persons={this.state.peopleFilter} />
      </div>
    );
  }
}
