import React, { Component } from "react";

import { SearchBar } from "./SearchBar";
import { List } from "./List";

export class InteractiveSection extends Component {
  state = {
    searchTerm: "",
    people: [],
    peopleFilter: []
  };

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then(response => response.json())
      .then(data => {
        this.setState({ people: data });
      });
  }

  handleChange = searchInput => {
    this.setState({ searchTerm: searchInput });
    const result = this.state.people.filter(person => {
      return person.name.toLowerCase() === searchInput.toLowerCase();
    });

    this.setState({ peopleFilter: result });
  };

  render() {
    return (
      <div className="Interactive">
        <h3>Fake JSON Database</h3>
        <SearchBar handleChange={this.handleChange} />
        {this.state.peopleFilter
          ? this.state.peopleFilter.map(p => <List person={p} key={p.id} />)
          : this.state.people.map(p => <List person={p} key={p.id} />)}
      </div>
    );
  }
}
