import React from "react";

import { BigCard } from "./BigCard";

export const InfoSection = props => {
  return (
    <div className="InfoSection">
      {props.item === null ? (
        <BigCard item={props.item} />
      ) : (
        <h1>Select a person</h1>
      )}
    </div>
  );
};
