import React from "react";

export const SearchBar = props => {
  handleInput = event => {
    const { value } = event.target;

    props.handleChange(value);
  };
  return (
    <input
      type="text"
      placeholder="Search for a character"
      onChange={handleInput}
    />
  );
};
